//logUpdate is expect to take all of these as parameters, whether it uses them or not.
//see runEffects in AbstractActionHandler
function logUpdate(state, payload, blockInfo, context) {
  console.info("State updated:\n", JSON.stringify(state, null, 2))
}

const effects = [
  {
		//what action does this effect map to?
    actionType: "eosio.token::transfer",
		//what to do when we get this action.
    effect: logUpdate,
  },
]

module.exports = effects
